let small = "plr-2 pbt-1 f-4 "
let medium = "plr-2 pbt-2 f-4 "
let large = "p-3 f-4 "

export const TextBtn = ({ children, onClick, className, variant, width,
    thick, color, size, round, disabled, type, style }) => {
    let sizeSet = !size ? small : size === "sm" ? small : size === "md" ? medium : size === "lg" ? large : small
    let roundSet = !round ? " bra-1" : ""
    return (
        <button onClick={onClick} disabled={!disabled == null ? false : disabled} type={type}
        className={!variant ? "clear " : variant + roundSet + " btn ai-c " + sizeSet + className}
            style={{
                /* borderColor: color,
                color: color, */
                width: !width ? "max-content" : width,
                borderWidth: !thick ? 1 : thick * 100,
                borderStyle: "solid",
                ...style
            }}>
            <p style={{
                fontWeight: !thick ? 300 : thick * 100
            }}>
                {!children ? "none" : children}
            </p>
        </button>
    )
}

export const LinkTextBtn = ({ children, onClick, href, className, variant, width, thick, color, size, round, style, disabled }) => {
    let sizeSet = !size ? small : size === "sm" ? small : size === "md" ? medium : size === "lg" ? large : small
    let roundSet = !round ? " bra-1" : ""
    return (
        <a href={!href ? null : href} onClick={onClick} disabled={!disabled == null ? false : disabled}
        className={!variant ? "clear" : variant + roundSet + " btn " + sizeSet + className}
            style={{
                height: "max-content",
                /* borderColor: color,
                color: color, */
                width: !width ? "max-content" : width,
                borderWidth: !thick ? 1 : thick * 100,
                borderStyle: "solid",
                ...style
            }}>
            <p style={{
                height: "max-content",
                fontWeight: !thick ? 1 : thick + 'em'
            }}>
                {!children ? "none" : children}
            </p>
        </a>
    )
}

/*
 * Read the blog post here:
 * https://letsbuildui.dev/articles/building-a-dropdown-menu-component-with-react-hooks
 */


export const DropdownBtn = ({ children, onSelect, value, list, variant, width, height,
    radius, thick, color, size, round, className }) => {
    let spanSize = !width || !height ? size == "sm" ? { height: "0.8rem"} : size === "md" ? { height: "1rem"} : size === "lg" ? { height: "1.2rem"} : { height: "0.8rem"} : {width: width, height: height}
    let sizeSet = !size ? small : size === "sm" ? small : size === "md" ? medium : size === "lg" ? large : small
    let roundSet = !round ? " bra-1" : ""
    const dropdownRef = useRef(null);
    const [isActive, setIsActive] = useDetectOutsideClick(dropdownRef, false);
    const onClick = () => setIsActive(!isActive);

    return (
        <div className={"container btn " + className}
        >
            <div className="menu-container w-100">
                <div onClick={onClick} className={ roundSet + " menu-trigger w-100" /* + sizeSet */ + " btn " + variant}
                    style={{
                        borderRadius: !radius ? "" : radius,
                        borderColor: color,
                        color: color,
                        borderWidth: !thick ? 1 : thick + 'px',
                        borderStyle: "solid",
                    }}>
                    <span style={spanSize}
                    ><b className="mr-1">{!isActive ? ">" : "v"}</b>{value}</span>
                </div>
                <nav
                    ref={dropdownRef}
                    className={`menu ${isActive ? "active" : "inactive"}`}
                >
                    <ul>
                        {!list ? 
                        <li>
                            {children}
                        </li> 
                        : list.map((x, index) => {
                            return <li key={index}>
                                <div className="btn" onClick={() => { onSelect(x); onClick() }}>{x}</div>
                            </li>
                        })}
                    </ul>
                </nav>
            </div>
        </div>
    );
}

//Toggle Switchs
export const ToggleSwitch = ({ onSwitch, value, className, variant, fieldList }) => {
    let width = 100 / fieldList.length.toString() + "%"
    return (
        <div className={className + " fieldList df " + variant}>
            {fieldList.map((x, index) => {
                return <div onClick={() => { onSwitch(x.count) }}
                    className="btn" style={{ width: width, backgroundColor: x.count == value ? "rgb(101, 163, 255, .5)" : "" }} key={index}>
                    {x.text}
                </div>
            })}
        </div>
    )
}

/*
Toggle Switch Component
https://github.com/sitepoint-editors/ToggleSwitch
Note: id, checked and onChange are required for ToggleSwitch component to function.
The props name, small, disabled and optionLabels are optional.
Usage: <ToggleSwitch id={id} checked={value} onChange={checked => setValue(checked)}} />
*/

export const ToggleSlideSwitch = ({ id, name, checked, onChange, optionLabels, small, disabled, style }) => {
    function handleKeyPress(e) {
        if (e.keyCode !== 32) return;

        e.preventDefault();
        onChange(!checked)
    }

    return (
        <div className={"toggle-switch" + (small ? " small-switch" : "")} style={style}>
            <input
                type="checkbox"
                name={name}
                className="toggle-switch-checkbox"
                id={id}
                checked={checked}
                onChange={e => onChange(e.target.checked)}
                disabled={disabled}
            />
            {id ? (
                <label className="toggle-switch-label"
                    htmlFor={id}
                    tabIndex={disabled ? -1 : 1}
                    onKeyDown={(e) => { handleKeyPress(e) }}>
                    <span
                        className={
                            disabled
                                ? "toggle-switch-inner toggle-switch-disabled"
                                : "toggle-switch-inner"
                        }
                        data-yes={optionLabels[0]}
                        data-no={optionLabels[1]}
                        tabIndex={-1}
                    />
                    <span
                        className={
                            disabled
                                ? "toggle-switch-switch toggle-switch-disabled"
                                : "toggle-switch-switch"
                        }
                        tabIndex={-1}
                    />
                </label>
            ) : null}
        </div>
    );
}

// Set optionLabels for rendering.
ToggleSlideSwitch.defaultProps = {
    optionLabels: ["Yes", "No"],
};

ToggleSlideSwitch.propTypes = {
    id: PropTypes.string.isRequired,
    checked: PropTypes.bool.isRequired,
    onChange: PropTypes.func.isRequired,
    name: PropTypes.string,
    optionLabels: PropTypes.array,
    small: PropTypes.bool,
    disabled: PropTypes.bool
};

